package com.axp.dao;import com.axp.domain.ReBaseGoods;import com.axp.domain.ReGoodsOfBase;public interface ReBaseGoodsDAO extends IBaseDao<ReBaseGoods> {    /**     * 根据基础商品，和给定的商城商品类型，寻找这个对应的商城商品类型；     *     * @param baseGoods 基础商品对象；     * @param clazz     商城商品类型；     * @param <T>       商城商品泛型；     * @return 返回商城商品对象；     */    <T> T getMallObjByBaseGoods(ReGoodsOfBase baseGoods, Class<T> clazz);    /**     * 根据商品编号来找出这个商品对象；     *     * @param goodsOrder 商品的编号；     * @return 返回对象（如果没有则是null，如果是多个则直接抛出一个错误；）     */    Object getMallObjByGoodsOrder(String goodsOrder) throws Exception;    /**     * 添加销量     * @param goodsOrder 商品的编号     * @param amount 销售个数     * @throws Exception     */	void addGoodsSalesVolume(String goodsOrder, Integer amount)			throws Exception;	Integer updateRepertory(String standardRepertory,			Integer noStandardRepertory, String goodsOrder) throws Exception;		void addGoodsSalesVolumeNew(Integer sellerMallId,Integer amount)  throws Exception;}